SELECT i.REST_ID, REST_NAME, FOOD_TYPE, FAVORITES, ADDRESS, round(avg(r.REVIEW_SCORE), 2) as "SCORE" from REST_INFO as i left join REST_REVIEW as r on i.REST_ID = r.REST_ID where r.REVIEW_SCORE is not null and ADDRESS like "서울%" group by i.REST_ID order by round(avg(r.REVIEW_SCORE), 2) desc, FAVORITES desc;